sys_hub_input_scripts.script:
{"max_subtotal":{"scriptActive":true,"script":"/*
**Access Flow/Action data using the fd_data object. Script must return a value.
**example: var shortDesc = fd_data.trigger.current.short_description;
**return shortDesc;
*/


var secretShop = new x_autg_kbb_secret.secretshopUtilities();
var sectionOrder = fd_data.trigger.current.subtotal_for_section.order;// SubTotal's section's order
var obj = fd_data.trigger.current;


var maxsubtotal = secretShop.calMaxSubTotalQuestion(obj,sectionOrder,fd_data.trigger.table_name);
return maxsubtotal;"}}

{"total_points":{"scriptActive":true,"script":"/*
**Access Flow/Action data using the fd_data object. Script must return a value.
**example: var shortDesc = fd_data.trigger.current.short_description;
**return shortDesc;
*/
var totalPoints;
 totalPoints = fd_data._1__look_up_record.record.points_available + fd_data.trigger.current.max_score;
gs.info("SecretShop: Calculate total points after new question is created = "+ totalPoints);
return totalPoints;



"}}

{"total_bonus_points":{"scriptActive":true,"script":"/*
**Access Flow/Action data using the fd_data object. Script must return a value.
**example: var shortDesc = fd_data.trigger.current.short_description;
**return shortDesc;
*/

var totalBonusPoints;
 totalBonusPoints = fd_data._1__look_up_record.record.bonus_points_available + fd_data.trigger.current.max_score;
gs.info("SecretShop: Calculate total bonus pts after creation of new question in a template = "+ totalBonusPoints);
return totalBonusPoints;"}}

