var tableName = fd_data.trigger.table_name;
var secretShop = new x_autg_kbb_secret.secretshopUtilities();
var subTotalMax =0;
subTotalMax = secretShop.updateMaxSubTotalQuestion(obj,preQuesMax,newQuesMax,tableName);
return subTotalMax;

"}}

